logger = logging.getLogger(__name__)
_chatbot = None
_retriever = None

def set_chatbot(chatbot, retriever=None):
    global _chatbot, _retriever
    _chatbot = chatbot
    _retriever = retriever

def generate_demo_response(message: str) -> str:
    message_lower = message.lower()
    responses = {'taylor swift': "Taylor Swift is an American singer-songwriter known for pop and country music. She has released albums like '1989', 'Red', 'Folklore', and 'Midnights'.", 'ed sheeran': "Ed Sheeran is a British singer-songwriter. His popular albums include '+', 'x', '√∑', and '='.", 'genre': 'I can help you find information about music genres! Popular genres include Pop, Rock, Hip-Hop, R&B, Country, Jazz, and Electronic.', 'album': 'I can tell you about albums! What specific album or artist are you interested in?', 'song': "I'd be happy to help with song information! Which song or artist would you like to know about?", 'bts': 'BTS (Î∞©ÌÉÑÏÜåÎÖÑÎã®) is a South Korean boy band formed in 2013. Members include RM, Jin, Suga, J-Hope, Jimin, V, and Jungkook.', 'beyonc√©': 'Beyonc√© is an American singer and actress. She has won numerous Grammy awards.', 'beyonce': 'Beyonc√© is an American singer and actress. She has won numerous Grammy awards.', 'grammy': 'The Grammy Awards are annual music awards presented by the Recording Academy.', 'pop': 'Pop music is a genre of popular music. Popular pop artists include Taylor Swift, Ed Sheeran, and Ariana Grande.', 'rock': 'Rock music is a broad genre that originated in the 1950s.', 'hello': "Hello! I'm the Music Knowledge Graph Chatbot. Ask me about artists, albums, songs, or genres!", 'hi': 'Hi there! Ask me anything about music - artists, albums, songs, genres, and more!'}
    for keyword, response in responses.items():
        if keyword in message_lower:
            return response
    return 'Welcome to the Music Knowledge Graph Chatbot! üéµ\n\nI\'m currently running in demo mode. Ask me about:\n- Artists (Taylor Swift, Ed Sheeran, BTS, Beyonc√©...)\n- Albums and songs\n- Music genres (Pop, Rock, Hip-Hop...)\n- Grammy awards\n\nTry asking: "What genre does Taylor Swift play?" '

def chat_respond(message: str, history: list) -> str:
    if not message.strip():
        return ''
    try:
        if _chatbot is not None:
            if _retriever is not None:
                result = _chatbot.answer_with_graph_context(message, _retriever, max_hops=2)
                answer = result['answer']
            else:
                answer = _chatbot.generate(message)
            answer = re.sub('<think>.*?</think>', '', answer, flags=re.DOTALL).strip()
        else:
            answer = generate_demo_response(message)
    except Exception as e:
        logger.error(f'Error generating response: {e}')
        answer = f'Sorry, an error occurred. Please try again.'
    return answer

